INSERT INTO public.advertisement (id, title, description, price, "saleStatus", photo, "categoryId", "vendorId", "buyerId") VALUES (2, 'Скалолазный рюкзак', 'Продуманная система хранения зарядных устройств, шнуров, мелкой электроники, ручек и других вещей. Компактный органайзер для путешествий для хранения кабелей и аксессуаров и легкого доступа к ним в дороге. ', 40, false, 'bag.webp', 4, 2, NULL);
INSERT INTO public.advertisement (id, title, description, price, "saleStatus", photo, "categoryId", "vendorId", "buyerId") VALUES (3, 'Руль', 'Оптимальная форма руля увеличивает комфорт от вождения, а так же повышают четкость управления автомобилем как при повседневной езде, так и при участие в различных соревнованиях.', 75, false, 'steering_wheel.jpg', 1, 6, NULL);
INSERT INTO public.advertisement (id, title, description, price, "saleStatus", photo, "categoryId", "vendorId", "buyerId") VALUES (4, 'Сковорода антипригарная', 'Сковорода VIATTO предназначена для приготовления широкого спектра блюд на индукционных и газовых плитах. Сковорода для индукционной плиты рекомендуется к применению на предприятиях общественного питания и торговли, в ресторанах, кафе, HoReCa, а также в домашних условиях. Это посуда, без которой невозможно представить приготовление вкусных блюд. Если вы ищете универсальную сковородку, то стоит обратить внимание на нержавеющую сталь. ', 30, false, 'pan.jpg', 4, 5, NULL);
INSERT INTO public.advertisement (id, title, description, price, "saleStatus", photo, "categoryId", "vendorId", "buyerId") VALUES (5, 'Кошачий корм', 'Состав: злаки и продукты их переработки, муки животного происхождения, гидролизат куриного белка, животные жиры и растительные масла (в том числе подсолнечное масло), свекловичный жом, соль, минеральные вещества, витамины, аминокислоты (в том числе таурин), красители, антиоксиданты, морковь сушёная. Содержание питательных веществ (100 г): белок – 26 г; жир – 10 г; клетчатка – 3 г; зола –  9 г; кальций –  1,5 г; фосфор –  0,9 г; витамин А –  1200 МЕ; витамин Д –  120 ME; витамин Е –  5,5 мг. Энергетическая ценность (100 г): 340 ккал / 1422 кДж.', 8, false, 'cat_food.jpg', 5, 2, NULL);
INSERT INTO public.advertisement (id, title, description, price, "saleStatus", photo, "categoryId", "vendorId", "buyerId") VALUES (6, 'Торшер', 'Напольный торшер из фанеры от бренда Roombliss белого матового цвета — элегантное сочетание эко стиля и уюта для вашего дома! Напольный светильник отлично подходит для дополнения в современных интерьерах в стилях скандинавский, лофт, винтаж, классический и других. Торшер с абажуром прекрасно впишется в спальню, гостиную, детскую, прихожую и даже в кабинет.', 23, false, 'floor_lamp.jpg', 2, 1, NULL);
INSERT INTO public.advertisement (id, title, description, price, "saleStatus", photo, "categoryId", "vendorId", "buyerId") VALUES (7, 'Arduino UNO', 'Контроллер Arduino Uno R3 - это популярная открытая платформа для прототипирования и создания электронных проектов. Он оснащен микроконтроллером ATmega328P, который обеспечивает широкие возможности для подключения датчиков, исполнительных устройств и других компонентов. Контроллер имеет разнообразные цифровые и аналоговые входы/выходы, что делает его универсальным инструментом как для начинающих, так и для опытных разработчиков.', 23, false, 'arduino.webp', 4, 6, NULL);
INSERT INTO public.advertisement (id, title, description, price, "saleStatus", photo, "categoryId", "vendorId", "buyerId") VALUES (8, 'Туфли мужские', 'Данный товар является частью проекта Lamoda planet - специального раздела нашего каталога, где мы собрали экологичные, этичные, инклюзивные и благотворительные товары. Товар произведен в стране присутствия Lamoda, что позволяет поддерживать развитие локальных фабрик, мастеров и швей. Покупая этот товар, вы также вносите свой вклад в сокращение углеродного следа.', 112, false, 'shoes.webp', 3, 1, NULL);
INSERT INTO public.advertisement (id, title, description, price, "saleStatus", photo, "categoryId", "vendorId", "buyerId") VALUES (9, 'Мышь компьютерная', 'Беспроводная мышь со встроенной подсветкой и аккумулятором - это высокотехнологичный компьютерный аксессуар, который обеспечивает безупречную работу и максимальный комфорт. Имеет радиус работы до 10 м от самого компьютера. Можно использовать, как игровую мышь для компьютера и ноутбука. Мышка для ноутбука и пк легко помещается в любой рюкзак, карман за счет своих небольших размеров. Этот гаджет подойдет как для работы с документами или в интернете, так и для игр. Вы можете выбрать комфортную чувствительность при помощи регулировки режима dpi (800/1200/1600), Аккумуляторная мышка имеет оптический модуль, благодаря которому она работает точно и плавно, а также подключается к компьютеру через Bluetooth (для устройств без USB) порта или USB-адаптер, функционирующий через Wifi (Вайфай). ', 24, false, 'computer_mouse.webp', 7, 4, NULL);
INSERT INTO public.advertisement (id, title, description, price, "saleStatus", photo, "categoryId", "vendorId", "buyerId") VALUES (23, 'Набор теннисных ракеток', 'Когда речь заходит о спортивных играх, настольный теннис - это один из самых популярных видов развлечений. Если вы хотите попробовать свои силы в этой увлекательной дисциплине, то набор для настольного тенниса - это отличный выбор для начинающих и опытных спортсменов.', 20, false, 'tennis_rockets.webp', 8, 1, NULL);
INSERT INTO public.advertisement (id, title, description, price, "saleStatus", photo, "categoryId", "vendorId", "buyerId") VALUES (10, 'Чехол для телефона', 'Чехол для телефона: надежная защита и стильный аксессуар. Чехол - идеальное решение для тех, кто хочет обеспечить надежную защиту своему устройству, не утратив при этом стильности и эстетической привлекательности. Предлагаем широкий выбор чехлов для различных моделей смартфонов, включая популярные бренды. Ключевые особенности чехлов для телефона, представленных в нашем ассортименте: 1. Полная защита: чехол закрывает заднюю панель и боковые грани устройства, обеспечивая надежную защиту от царапин, сколов и пыли. Это позволяет сохранить ваш телефон в идеальном состоянии на протяжении всего его срока службы. 2. Прочность и долговечность: используются только высококачественные материалы при производстве чехлов, чтобы обеспечить максимальную прочность и долговечность. Это гарантирует, что ваш чехол будет служить вам долгие годы, не теряя первоначального вида.', 35, false, 'phone_case.webp', 7, 3, NULL);
INSERT INTO public.advertisement (id, title, description, price, "saleStatus", photo, "categoryId", "vendorId", "buyerId") VALUES (11, 'Шапка Деда Мороза', 'Эта волшебная шапка Деда Мороза просто необходима для создания праздничного настроения! Она выполнена из плотного материала, который идеально держит форму, а искусственный мех на основании придает ей особый шарм. Нарядитесь в эту прекрасную шапку, и вы точно порадуете как взрослых, так и детей! Веселый предмет гардероба самого любимого на свете Дедушки поможет вам воссоздать нужную атмосферу праздника и веселья. Эта новогодняя шапка универсальна и подходит как девушкам, так и парням. Так что не упустите возможность добавить кусочек волшебства в свою жизнь! Материал: текстиль, искусственный мех.', 6, false, 'santa_clouse_cap.webp', 3, 1, NULL);
INSERT INTO public.advertisement (id, title, description, price, "saleStatus", photo, "categoryId", "vendorId", "buyerId") VALUES (12, 'Браслет на руку, мужской', 'Мужской браслет, с узором травы Тан, представляет собой оригинальный дизайн в стиле ретро. Этот модный аксессуар дополнит ваш образ, придаст ему особый шарм и выразит вашу индивидуальность. Браслет подходит как для мужчин, так и для женщин. Толщина его составляет около 2,5 мм, что делает его достаточно прочным и надежным. Регулировка размера позволяет подобрать идеальную посадку на руку, подходящую для окружности руки около 15-18 см. Изготовленный из высококачественного материала, этот браслет обещает быть долговечным и сохранить свой прекрасный вид на протяжении длительного времени. Носите его с уверенностью и добавьте немного властности своему стилю.', 13, false, 'man_bracelet.webp', 8, 5, NULL);
INSERT INTO public.advertisement (id, title, description, price, "saleStatus", photo, "categoryId", "vendorId", "buyerId") VALUES (13, 'Кроссовки Air Jordan', 'Эти кроссовки представляют собой яркий и элегантный дизайн, который привлекает внимание всех окружающих. Кроме того, благодаря высокому качеству материалов и инновационной технологии изготовления, они обеспечивают максимальный комфорт и поддержку во время активных тренировок или повседневного использования.', 80, false, 'jordans.webp', 3, 3, NULL);
INSERT INTO public.advertisement (id, title, description, price, "saleStatus", photo, "categoryId", "vendorId", "buyerId") VALUES (14, 'Аквариум с подсветкой', 'Привлекательный дизайн, цветовые решения и их компактный размер позволят органично вписать аквариум в любой интерьер. Благодаря переднему изогнутому стеклу и пластиковым накладкам аквариум Барбус не имеет видимых клеевых швов. Он оборудован откидной крышкой и отдельным люком для кормления, что делает его обслуживание максимально удобным и быстрым. Для того чтобы вода всегда оставалась чистой, в аквариуме предусмотрена система фильтрации Top-filter с дополнительной аэрацией. Она включает в себя подъемную и заборную помпы, две выходных насадки, отсек для фильтрации и шланг для подачи кислорода. Заборная насадка фильтра позволяет избежать попадания в помпу крупных частиц мусора и предотвращает засасывание мелких аквариумных обитателей. Помпа поднимает воду наверх, а выходная насадка-флейта равномерно распределяет её по всей площади фильтрующего элемента.', 140, false, 'aquarium.jpeg', 5, 4, NULL);
INSERT INTO public.advertisement (id, title, description, price, "saleStatus", photo, "categoryId", "vendorId", "buyerId") VALUES (15, 'Акустика OklickOK-127', 'Компактные компьютерные колонки со светодиодной подсветкой. Акустическая система займет минимум места на рабочем столе Питание от порта USB - Колонки не требуют сетевого источника питания. Просто подключите колонки к USB-разъему компьютера и наслаждайтесь звуком. Колонки необходимо подключить также к аудиоразъему вашего персонального компьютера, поскольку данная модель не оборудована интегрированной звуковой картой Регулятор громкости на кабеле - Вам не придется переключаться на управление компьютером, чтобы отрегулировать уровень громкости.  Длина кабеля питания: 1.05 м. Длина AUX-кабеля: 1.05 м. Длина кабеля между сателлитами: 0.74 м', 220, false, 'computer_speakers.webp', 7, 1, NULL);
INSERT INTO public.advertisement (id, title, description, price, "saleStatus", photo, "categoryId", "vendorId", "buyerId") VALUES (16, 'Набор ручек', 'Капиллярные цветные ручки (линеры) сильно отличаются от маркеров с пулевидным пишущим узлом. Линия письма линеров для рисования намного тоньше и изящней. Такой лайнер для рисования можно использовать для написания красивых текстов, раскрашивания картинок, создания скетчей, обводить рисунки, и заниматься более тонкой творческой работой. Капиллярные ручки с металлическим пишущим наконечником диаметром 0.4 мм изготовлены из высококачественного пластика и абсолютно безопасны для детского творчества, использования дома, в школе, в офисе. Разноцветные ручки обладают яркими, стойкими цветами.  Удобный корпус цветных линеров - хороший выбор для работы и творчества. ', 19, false, 'pens.webp', 2, 6, NULL);
INSERT INTO public.advertisement (id, title, description, price, "saleStatus", photo, "categoryId", "vendorId", "buyerId") VALUES (17, 'Экспандер кистевой', 'Пожалуйста, ЗамеФитнес - это не просто тренировка, это образ жизни, спортивная форма и забота о здоровье. Для достижения успеха в мире спорта, йоги, гимнастики, бокса и других видов тренировок, необходим хороший тренажер. Эспандер кистевой для фитнеса - идеальный выбор для поддержания формы и укрепления рук.ните этот текст Вашим собственным. Просто кликните по тексту, чтобы добавить свой текст. Настройте стиль текста в левой колонке.', 19, false, 'hand_trainer.webp', 8, 2, NULL);
INSERT INTO public.advertisement (id, title, description, price, "saleStatus", photo, "categoryId", "vendorId", "buyerId") VALUES (18, 'Покрышка велосипедная', 'Покрышка для велосипеда Continental Ultra Sport III - это самая популярная шоссейная покрышка с полностью обновленным дизайном протектора на рынке, в ней были сильно доработаны дорожки для водоотведения. Данная модель подходит как для шоссейных, так и для быстрых городских велосипедов. Компаунд резины PureGrip, созданный на основе соединения диоксида кремния, значительно повышает эксплуатационные характеристики и особенно качество сцепления с покрытием.', 30, false, 'bicycle_tire.webp', 8, 5, NULL);
INSERT INTO public.advertisement (id, title, description, price, "saleStatus", photo, "categoryId", "vendorId", "buyerId") VALUES (20, 'Каляска', 'умка (органайзер) для коляски прогулочной под детские аксессуары один из самых востребованных и необходимых товаров для мамы и малыша. Вы обязательно возьмете ее с собой когда отправитесь на прогулку с ребенком. Наша универсальная сумочка подходит для хранения запасных памперсов (подгузников), одноразовых пеленок, влажных салфеток, игрушек ребенка, и других акссесуаров и принадлежностей. Оснащена отсеками для бутылочек и карманом для телефона (смартфона), а так же надежными застежками для крепления к ручке каляски. Если ваш ребенок захочет поесть, то под рукой всегда будут бутылки со смесью, а когда он захочет поиграть вы сможете предложить ему игрушечную машинку или куклу. Подходит как для мальчиков, так и для девочек. Поможет вам на прогулке в любое время года, будь то зима или лето. Имеет 6 отсеков для вещей. Используемая ткань - полиэстер. ', 90, false, 'stroller.webp', 6, 1, NULL);
INSERT INTO public.advertisement (id, title, description, price, "saleStatus", photo, "categoryId", "vendorId", "buyerId") VALUES (21, 'Пижама Акула', 'Необыкновенная природа открыла перед нами увлекательный мир животных, и одно из них - акула. Это настолько интересные существа, что обрели они свое место не только в океане, но и в нашей модной коллекции. Для детей и взрослых представляем кигуруми пижаму Акула, которая превратит ваш дом в комфортное гнездышко. Этот костюм для девочки был создан с целью удобства и стиля - все, что нужно для отдыха и хорошего настроения. Кигуруми пижама Акула стала самой полюбившейся одеждой для дома. Она приносит тепло и комфорт, а ощущение уюта играет с контрастами: заостренные линии элементов на этом костюме сочетаются с мягкогим и приятным материалом. Это модное решение делает пижаму Акула неотъемлемой частью гармоничного образа. Сочетание легкости и качественной отделки создает непередаваемое ощущение абсолютного спокойствия. Кигуруми пижама Акула - это возможность совместно чувствовать свободу и уют без излишеств. Эта нежная одежда призвана расслабить ваше тело после интенсивного дня и создать неповторимую атмосферу, наполненную радостью и безмятежностью. ', 25, false, 'shark_pajamas.webp', 6, 5, NULL);
INSERT INTO public.advertisement (id, title, description, price, "saleStatus", photo, "categoryId", "vendorId", "buyerId") VALUES (22, 'Робот-пылесос', 'Робот-пылесос VR32G02MW возьмет на себя всю сухую и влажную уборку полов. Благодаря гироскопической системе навигации и управлению с помощью смартфона устройство будет отлично ориентироваться в пространстве и избавит вас от домашней работы. Выбирайте режимы Настраивая пылесос, вы можете сами направлять устройство через приложение или выбрать один из режимов: точечная уборка, уборка вдоль стен или уборка зиг-загом. Турбощетка выметет всю пыль из щелей, а две боковые щетки обеспечат полную чистоту. ', 145, false, 'robot_vacuum_cleaner.webp', 7, 2, NULL);
INSERT INTO public.advertisement (id, title, description, price, "saleStatus", photo, "categoryId", "vendorId", "buyerId") VALUES (24, 'Антифриз HEPU P900RM12', 'Антифриз Hepu P900RM12 красный является высококачественным охлаждающим средством, разработанным для использования в системах охлаждения автомобилей. Красный антифриз Хепу П900 РМ12 обеспечивает надежную защиту двигателя от перегрева и замерзания при экстремальных температурах. Охлаждающая житкость Хепу красный 12 включает специальные ингредиенты, предназначенные для максимальной эффективности и долговечности. Hepu красный предлагает смесь основных органических компонентов, а также добавки, которые помогают предотвратить коррозию и накипь в системе охлаждения.', 20, false, 'antifreeze.webp', 1, 4, NULL);
INSERT INTO public.advertisement (id, title, description, price, "saleStatus", photo, "categoryId", "vendorId", "buyerId") VALUES (25, 'Клавиатура игровая', 'роводная игровая клавиатура Defender Doom Keeper GK-100DL RU - это ультимативное решение для геймеров, которые ищут надежность, комфорт и функциональность во время игры. Эта клавиатура обладает рядом особенностей, которые помогут вам достичь максимальных результатов в игровом мире. Специальные игровые функции, такие как блокировка системных клавиш Windows и переключение стрелок курсора на кнопки WASD, позволяют избежать нежелательного вылета из игры и обеспечить более удобное и интуитивное управление персонажем. Клавиатура обладает 3 цветами подсветки, что создает эффектный визуальный эффект и позволяет настроить атмосферу под ваш стиль игры. Вы также можете регулировать яркость подсветки, чтобы найти оптимальное сочетание цветов. Горячие клавиши клавиатуры Doom Keeper GK-100DL RU позволяют быстро получить доступ к различным приложениям, упрощая вашу работу и игровой процесс. Вы сможете мгновенно выполнять действия без необходимости искать нужные приложения или команды. Клавиатура также обладает подсветкой символов и клавиш, что делает ее легко читаемой даже в темных условиях. Вы также можете выбрать один цвет подсветки или выключить подсветку вовсе, в зависимости от вашего предпочтения. Угол наклона клавиатуры регулируется с помощью ножек, что позволяет вам настроить ее для наилучшего удобства во время игры. Вы сможете найти идеальное положение, которое позволит вам играть наиболее эффективно и комфортно.', 27, false, 'keyboard.webp', 7, 3, NULL);
INSERT INTO public.advertisement (id, title, description, price, "saleStatus", photo, "categoryId", "vendorId", "buyerId") VALUES (26, 'Миска для собак', 'Универсальная круглая миска для собак выполнена из нержавеющей стали. Она идеально подходит как для воды, так и для корма разной консистенции. Миска комфортна для питомца и легко моется под струей проточной воды с использованием моющих бытовых средств. Размеры: 210*210*75мм.', 27, false, 'dog_bowl.webp', 5, 6, NULL);
INSERT INTO public.advertisement (id, title, description, price, "saleStatus", photo, "categoryId", "vendorId", "buyerId") VALUES (27, 'Настольный футбол', 'Игровой досуг – это то, что объединяет и радует как взрослых, так и детей. И в нашем ассортименте настольных игр есть прекрасный выбор для всей семьи – настольная игра ''Футбол''. Эта увлекательная игра предназначена для двоих игроков и позволяет в полной мере погрузиться в настольный футбол. Настольные игры для детей – это не только развлечение, но и увлекательное обучение. Наша спортивная настольная игра подарит много положительных эмоций как детям, так и взрослым. Это прекрасный подарок мальчику. Настольная игра ''Футбол'' выполнена из дерева – экологически чистого и качественного материала. Мы приложили максимум усилий, чтобы игровое поле было идеально гладким и качественным. Это не только обеспечит комфорт и превосходные игровые возможности, но и подчеркнет безопасность игры для детского здоровья. ', 42, false, 'table_soccer.jpg', 6, 1, NULL);
INSERT INTO public.advertisement (id, title, description, price, "saleStatus", photo, "categoryId", "vendorId", "buyerId") VALUES (28, 'Шуба NIKOLAI ZHYVITSA', 'Для стильных и активных - ШУБКА из меха натуральной куницы. С ней Вы даже не задумаетесь «под что же ее носить». Она отлично вписывается в стиль casual, но не прочь покрасоваться и на красной дорожке. Воротник-стойка - это не только модный акцент, но и защита для Вашей шеи. И дополнительные аксессуары не нужны. Длина 55 см - удобна для авто. Не надо все время приподнимать шубку, подворачивать, чтобы сесть. Застегивается в стык, надежные итальянские крючки, удобные карманы, приятная подкладка.', 70, false, 'fur_coat.webp', 3, 2, NULL);
INSERT INTO public.advertisement (id, title, description, price, "saleStatus", photo, "categoryId", "vendorId", "buyerId") VALUES (29, 'Кресло-качалка Аттика', 'Очень удобное, недорогое и практичное, складное кресло-качалка! Складывается и раскладывается за считанные секунды без применения специальных инструментов, помещается в любой автомобиль. Очень компактное и не доставляет проблем при хранении и транспортировке. Форма кресла повторяет линию изгиба спины сидящего человека, разгружает и расслабляет мышцы спины и позвоночника. Каркас выполнен из легкой и прочной стальной трубки и, несмотря на легкость конструкции, кресло выдерживает достаточно большую нагрузку.', 18, false, 'rocking_chair.webp', 4, 4, NULL);
INSERT INTO public.advertisement (id, title, description, price, "saleStatus", photo, "categoryId", "vendorId", "buyerId") VALUES (30, 'Haylou Умные часы LS02', 'Смарт-часы Haylou LS02 - это стильные и функциональные умные часы, которые предлагают широкий спектр возможностей для отслеживания активности, контроля здоровья и повседневного использования. Вот некоторые основные характеристики и особенности этих смарт-часов: 1. Дизайн и экран: Смарт-часы Haylou LS02 имеют современный и элегантный дизайн, который подойдет к любому стилю. Устройство оснащено круглым дисплеем с сенсорным управлением, обеспечивающим удобство при просмотре информации. 2. Функциональность: Часы поддерживают множество полезных функций, включая отслеживание физической активности (шагомер, калории, дистанция), измерение пульса, качества сна, уведомления о звонках и сообщениях, управление музыкой и камерой на смартфоне. 3. Водонепроницаемость: Haylou LS02 обладают защитой от воды и пыли по стандарту IP68, что позволяет носить часы даже во время спортивных занятий и при влажных условиях. 4. Автономность: Смарт-часы обеспечивают отличное время автономной работы благодаря емкому аккумулятору, что обеспечивает долгую работу без необходимости постоянной подзарядки. Смарт-часы Haylou LS02 - это прекрасный выбор для тех, кто ценит сочетание стильного дизайна, обширных функциональных возможностей и удобства использования.', 100, false, 'smart_watches.webp', 3, 6, NULL);
INSERT INTO public.advertisement (id, title, description, price, "saleStatus", photo, "categoryId", "vendorId", "buyerId") VALUES (19, 'Буксировочный трос', 'Лента буксировочная 2.5т*4м - это специальная лента, которую используются для буксировки различных грузов, для буксировки или эвакуации автомобилей и других транспортных средств. Она представляет собой прочную и высокопрочную ленту, специально разработанную для удержания большой массы транспортного средства при буксировке.', 30, false, 'tow_rope.webp', 1, 3, 4);
